import React, { useEffect, useState } from 'react'
import '../../../App.css'
import '../../../Styles/Dashboard.css'
import { faBell, faEllipsisV, faListUl, faSearch } from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Axios from 'axios'

export default function DashboardHeader(props) {

    const [openNotice, setOpenNotice] = useState(null)
    const [orderNotice, setOrderNotice] = useState(null)
    const [notice, setNotice] = useState(null)

    useEffect(()=>{
        if (props.orderNotice) {
            setOrderNotice(props.orderNotice)
        }
        Axios.get(`http://localhost:4000/notice`)
            .then(res => {
                setNotice(res.data)
            } 
        )
    },[])

    return (
        <div className="dashboard-header flex">
            <div className="flex-center">
                <div className="menu-opt flex-center"
                    onClick={props.setOpenMenuOnClick}>
                    { props.openMenu && <FontAwesomeIcon icon={faEllipsisV}/>}
                    { props.openMenu === false && <FontAwesomeIcon icon={faListUl}/>}
                </div>
                <p>{props.itemName}</p>
            </div>
            <div className="flex-center">
                <form className="menu-search flex">
                    <input type="text" placeholder="Search..."></input>
                    <div className="flex-center">
                        <FontAwesomeIcon icon={faSearch} className="icon"/>
                    </div>
                </form>
                <div 
                    className="menu-notice noselect"
                    onClick={()=>{
                        if (openNotice) {
                            setOpenNotice(false)
                        } else {
                            setOpenNotice(true)
                        }
                    }}
                >
                    <FontAwesomeIcon icon={faBell} style={{pointerEvents: 'none'}}/>
                    { openNotice && 
                        <div className="notice-box">
                            { notice && 
                                notice.map((item, index) => {
                                    return (
                                        <div>
                                            {item.noticeContent}
                                        </div>
                                    )
                                })
                            }
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}
