import React, { useEffect, useRef, useState } from 'react';
import '../../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faTimes } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios'

export default function Search(props) {  

    const [products, setProducts] = useState([])
    const [constProducts, setConstProducts] = useState([])
    const [searchInput, setSearchInput] = useState("")

    useEffect(()=> {
        axios.get(`http://pe.heromc.net:4000/products`)
            .then(res => {
                setProducts(res.data)
                setConstProducts(res.data)
            }
        )
    })

    const search = (event) => {
        const value = event.target.value
        setSearchInput(value)
        const search = []
        for (let i in constProducts) {
            if ((constProducts[i].productName).toLowerCase().includes(searchInput)) {
                search.push(constProducts[i])
            }
        }
        setProducts(search)
    }

    return(
        <div className={props.searchOpen === false ? 'Search displayNone' : 'Search'}>
            <div className="search-header flex">
                <div className="search-title">Search</div>
                <div
                    className="search-close"
                    onClick={props.clickToClose}
                    >
                    <FontAwesomeIcon 
                        icon={faTimes}
                        className="icon"
                        />
                </div>
            </div>
            <div className={props.searchOpen === false ? '' : 'fadeIn'}>
                <div 
                    className='search-tab flex'>
                    <div className="search-tab-cate search-tab-active">All Categories</div>
                    <div className="search-tab-cate">Woman</div>
                    <div className="search-tab-cate">Bags</div>
                    <div className="search-tab-cate">Search</div>
                    <div className="search-tab-cate">Bags</div>
                    <div className="search-tab-cate">Search</div>
                </div>

                <div className="search-form">
                    <form className="flex">
                        <FontAwesomeIcon icon={faSearch} className="icon"/>
                        <input 
                            placeholder="Search" 
                            onChange={search}
                            value={searchInput}
                        />
                        <FontAwesomeIcon icon={faTimes} className="icon"/>
                    </form>
                </div>
            </div>
        </div>
    ) 
}
