import React, { useState } from 'react';
import '../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import {  faAngleRight } from "@fortawesome/free-solid-svg-icons";
import { Link } from 'react-router-dom';

import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

export default function ProductBody(props) {

    function slugify(text){
        return text.toString().toLowerCase()
            .replace(/\s+/g, '-')           // Replace spaces with -
            .replace(/[^\u0100-\uFFFF\w\-]/g,'-') // Remove all non-word chars ( fix for UTF-8 chars )
            .replace(/\-\-+/g, '-')         // Replace multiple - with single -
            .replace(/^-+/, '')             // Trim - from start of text
            .replace(/-+$/, '');            // Trim - from end of text
    }

    const slugSex = "/" + slugify(props.productSex);

    const productImg = props.productImg;
    const [imgIndex, setImgIndex] = useState(0);

    const settings = {
        dots: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000
    }

    return(
        <div className="ProductBody">
            <div className="product-breadcrumb flex">
                <Link to="/" className="breadcrumb-item breadcrumb-link">Home</Link>
                <FontAwesomeIcon icon={ faAngleRight } className="breadcrumb-arrow"/>
                <Link to={slugSex} className="breadcrumb-item breadcrumb-link">{props.productCate}</Link>
                <FontAwesomeIcon icon={ faAngleRight } className="breadcrumb-arrow"/>
                <div className="breadcrumb-item breadcrumb-product">{props.productName}</div>
            </div>

            <div className="product-detail">
                {/* <div className="product-gallery flex">
                    <div className="product-small">
                        {productImg.map((item, index) => {
                            return (
                                <div className="product-small-item">
                                    <img 
                                        src={item}
                                        onClick={()=>{ setImgIndex(index)}}
                                        ></img>
                                </div>
                            )
                        })}
                    </div>
                    <div className="product-big">
                        <img src={productImg[imgIndex]}></img>
                    </div>
                </div> */}
                <div>
                    <h2>Slider Syncing (AsNavFor)</h2>
                    <h4>First Slider</h4>
                    <Slider
                    asNavFor={this.state.nav2}
                    ref={slider => (this.slider1 = slider)}
                    >
                    <div>
                        <h3>1</h3>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                    <div>
                        <h3>6</h3>
                    </div>
                    </Slider>
                    <h4>Second Slider</h4>
                    <Slider
                    asNavFor={this.state.nav1}
                    ref={slider => (this.slider2 = slider)}
                    slidesToShow={3}
                    swipeToSlide={true}
                    focusOnSelect={true}
                    >
                    <div>
                        <h3>1</h3>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                    <div>
                        <h3>6</h3>
                    </div>
                    </Slider>
                </div>
            </div>
        </div>
    )
}
