import React, { useEffect, useState } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck, faEdit, faTimes } from '@fortawesome/free-solid-svg-icons'
import axios from 'axios'

export default function DashboardTodoList(props) {
    const [todoList, setTodoList] = useState([])
    const [isChange, setIsChange] = useState(false)

    useEffect(()=>{
        axios.get(`http://localhost:4000/todos`)
            .then(res => {
                setTodoList(res.data)
            }
        )
    },[isChange])

    const clickToCheck = (event)=>{
        const target = JSON.parse(event.target.id)
        const id = target.id
        var isDone = ""
        if (target.isDone === true) {
            isDone = false
        } else {
            isDone = true
        }
        console.log(isDone)
        setIsChange(true)
        setTimeout(()=>{
            setIsChange(false)
        },10)
        axios.post('http://localhost:4000/todos', {
            id: id,
            isDone, isDone
        })
    }

    return (
        <div className="topfive flex-col" style={{width: '40%'}}>
            <div className={`headerbox flex-center ${props.color}`}>
                <FontAwesomeIcon icon={props.icon} className="icon"/>
            </div>
            <div className="top-location-container">
                <div className="headerbox-header">
                    <p>{props.title}</p>
                </div>
                <div className="topfive-content">
                    <div className="todo-list" style={{height: '390px', overflowY: 'scroll'}}>
                        <p className="todo-title">upcoming</p>
                        {todoList &&
                            todoList.map((item, index)=>{
                                return(
                                    <div
                                        key={index}
                                        className="topfive-div flex"
                                    >
                                        {
                                            item.isDone === false && 
                                            <div 
                                                className="todo-check"
                                                id={JSON.stringify({
                                                    id: item._id,
                                                    isDone: item.isDone
                                                })}
                                                onClick={clickToCheck}
                                                >
                                            </div>
                                        }
                                        {
                                            item.isDone === true && 
                                            <div 
                                                className="todo-check-done"
                                                id={JSON.stringify({
                                                    id: item._id,
                                                    isDone: item.isDone
                                                })}
                                                onClick={clickToCheck}
                                                > 
                                                <FontAwesomeIcon icon={faCheck} style={{pointerEvents: 'none', color: '#23bfea'}}/>
                                            </div>
                                        }
                                        <p className="todo-text">{item.todoContent}</p>
                                        <div className="todo-action flex">
                                            <div>
                                                <FontAwesomeIcon icon={faEdit} className="icon blue"/>
                                            </div>
                                            <div>
                                                <FontAwesomeIcon icon={faTimes} className="icon red" style={{marginTop: '5px', fontSize: '12px', color: '#ffb3b7', marginLeft: '7px'}}/>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        }
                        <p className="todo-title" style={{marginTop: '20px'}}>finished</p>
                    </div>
                </div>
            </div>
        </div>
    )
}
