import React, { useState } from 'react'
import '../../../App.css'
import '../../../Styles/Dashboard.css'
import { faFileInvoice, faMoneyBillWave, faTshirt, faUser } from '@fortawesome/free-solid-svg-icons'
import DashboardTotalCount from './DashboardTotalCount'
import DashboardLocation from './DashboardLocation'

export default function DashboardMain() {

    const totalCount = [
        {
            id: 1,
            title: "Total orders",
            count: "3200",
            percent: 12,
            color: "orange",
            icon: faFileInvoice
        },
        {
            id: 2,
            title: "Total sales",
            count: "$120 000",
            percent: 20,
            color: "pink",
            icon: faTshirt
        },
        {
            id: 3,
            title: "Income",
            count: "$30 000",
            percent: 30,
            color: "green",
            icon: faMoneyBillWave
        },
        {
            id: 4,
            title: "Customers",
            count: "1200",
            percent: 5,
            color: "green",
            icon: faUser
        },
    ]

    let total = 0;
    for (let i of locationList) {
        total += i.sold
    }
    const topLocationList = locationList.splice(0,5)

    return (
        <div className="dashboard-main">
            <DashboardLocation/>
            <div className="row flex">
                { totalCount.map((item, index)=> {
                    return (
                        <DashboardTotalCount
                            icon = {item.icon}
                            title = {item.title}
                            count = {item.count}
                            percent = {item.percent}
                            color = {item.color}
                        />
                    )
                })}
            </div>
        </div>
    )
}
