    //             isLogoWhite: true
    //         }) 
    //     }

    //     if (this.props.location === "/news") {
    //         if (this.scrollUp === true) {
    //             this.setState({
    //                 cc: true
    //             })
    //         } else {
    //             this.setState({
    //                 cc: false
    //             })
    //         }
    //     }

    //     if (isTop !== true) {
    //         this.setState({
    //             // scrolled: true,
    //             isWhite: true,
    //             isLogoWhite: false
    //         })
    //     } else { 
    //         this.setState({
    //             // scrolled: false,
    //             isLogoWhite: true
    //         })
    //     }

    //     if (this.prev > window.scrollY) { 
    //         this.setState({
    //             // scrolled: false,
    //             isLogoWhite: false
    //         })
    //         if (this.state.location === "/news") {
    //             this.setState({
    //                 scrollUp: true
    //             })
    //         }
    //     }
    //     this.prev = window.scrollY;
    // };

    // componentWillUnmount() {
    //     window.removeEventListener('scroll');
    // }

    render() {
        console.log(this.state.scrollUp);
        const {location , scrolled , isWhite, isLogoWhite, scrollUp} = this.state;
        return(
            <div 
                className={classNames('Header', {
                    scrolled: scrolled === true
                })}
                >
                <ul className="menu flex-center">
                    <li>
                        <Link 
                            to="/" 
                            className={classNames({
                                active: location === "/"
                            })}
                            id="1"
                            >home</Link>
                    </li>
                    <li>
                        <Link to="/news"
                            className={classNames({
                                active: location === "/news"
                            })}
                            id="2" 
                            >News</Link>
                    </li>
                    <li>
                        <Link to="/men" 
                            className={classNames({
                                active: location === "/men"
                            })}
                            id="3"
                            >men</Link>
                    </li>
                    <li>
                        <Link to="/women" 
                            className={classNames({
                                active: location === "/women",
                                whitelink_header: location === "/news",
                                blacklink_header: scrollUp === true
                            })}
                            id="4"
                            >women</Link>
                    </li>
                    <li>
                        <Link to="/contact" 
                            className={classNames({
                                active: location === "/contact",
                                whitelink_header: location === "/news",
                                blacklink_header: scrollUp === true
                            })}
                            id="5"
                            >contact</Link>
                    </li>
                </ul>
                <div className="logo flex-center">
                    <Link to="/">
                        {location !== "/news"
                            ?<img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo.svg" alt="logo"></img>
                            :(
                                isLogoWhite === true 
                                    ? <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo-light.svg" alt="logo"></img> 
                                    : <img src="https://demo.uix.store/sober/wp-content/themes/sober/images/logo.svg" alt="logo"></img>
                            )
                        }
                    </Link>
                </div>
                <div className="cart flex-center"> 
                    <FontAwesomeIcon icon={faSearch} className="icon"/>
                    <FontAwesomeIcon icon={faUser} className="icon"/>
                    <div className="icon flex-center">
                        <FontAwesomeIcon icon={faCartPlus} className="cart-icon"/>
                        <div
                            className={classNames("cart-count cart_count_news_hover", {
                                cart_count_news: location === "/news"
                            })}>
                            <p>0</p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default withRouter(Header);
