import React, { useEffect, useRef, useState } from 'react';
import '../../App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch, faTimes } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios'

export default function Search(props) {  

    const [products, setProducts] = useState([])
    const [constProducts, setConstProducts] = useState([])
    const [searchInput, setSearchInput] = useState("")

    useEffect(()=> {
        axios.get(`http://pe.heromc.net:4000/products`)
            .then(res => {
                setProducts(res.data)
                setConstProducts(res.data)
            }
        )
    })

    const search = (event) => {
        const value = event.target.value
        setSearchInput(value)
        const search = []
        for (let i in constProducts) {
            if ((constProducts[i].productName).toLowerCase().includes(searchInput)) {
                search.push(constProducts[i])
            }
        }
        setProducts(search)
    }

    console.log(products)

    return(
        <div className={props.searchOpen === false ? 'Search displayNone' : 'Search'}>
            <div className="search-header flex">
                <div className="search-title">Search</div>
                <div
                    className="search-close"
                    onClick={props.clickToClose}
                    >
                    <FontAwesomeIcon 
                        icon={faTimes}
                        className="icon"
                        />
                </div>
            </div>
            <div className={props.searchOpen === false ? '' : 'fadeIn'}>
                <div 
                    className='search-tab flex'>
                    <div className="search-tab-cate search-tab-active">All Categories</div>
                    <div className="search-tab-cate">Woman</div>
                    <div className="search-tab-cate">Bags</div>
                    <div className="search-tab-cate">Search</div>
                    <div className="search-tab-cate">Bags</div>
                    <div className="search-tab-cate">Search</div>
                </div>

                <div className="search-form">
                    <form className="flex">
                        <FontAwesomeIcon icon={faSearch} className="icon"/>
                        <input 
                            placeholder="Search" 
                            onChange={search}
                            value={searchInput}
                        />
                        <FontAwesomeIcon icon={faTimes} className="icon"/>
                    </form>
                </div>

                <div className="cart-item flex" key={index}>
                    <div className="cart-product-img">
                        <img src={item.productImg[0]} width="80px" height="100%" alt=""></img>
                    </div>
                    <div className="cart-product-mobile flex">
                        <div className="cart-product-name flex" style={{alignItems: 'center', justifyContent: 'flex-start'}}>{item.productName}</div>
                        <div className="cart-product-amount flex-center">
                            <div className="count-cart noselect">
                                <div 
                                    className="count-cart-item left flex-center"
                                    id={item._id}
                                    onClick={minusCount}
                                    >
                                    <FontAwesomeIcon  style={{pointerEvents: 'none'}} icon={faMinus}/>
                                </div> 
                                <div className="count-cart-item text flex-center">
                                    <form style={{width: '100%', margin: '0', height: '30px'}}>
                                        <input 
                                            style={{width: '100%', margin: '0', height: '30px'}}
                                            type="text" 
                                            value={item.count}
                                            id={item._id}
                                            onChange={updateCount}
                                        />
                                    </form>
                                </div>
                                <div 
                                    className="count-cart-item right flex-center"
                                    id={item._id}
                                    onClick={plusCount}
                                    >
                                    <FontAwesomeIcon  style={{pointerEvents: 'none'}} icon={faPlus}/>
                                </div>
                            </div>
                        </div>
                        <div className="cart-product-price flex" style={{alignItems: 'center', justifyContent: 'center'}}>{item.productPrice.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".")} đ</div>
                        <div className="cart-product-totalprice flex" style={{alignItems: 'center', justifyContent: 'center'}}>{(item.productPrice * item.count).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".")} đ</div>
                        <div className="cart-product-delete"
                        onClick={removeFromCart}
                        id={item._id}>
                        <FontAwesomeIcon style={{pointerEvents: 'none'}} icon={faTimes}/>
                    </div>
                    </div>
                </div>
            </div>
        </div>
    ) 
}
