import React, { useEffect, useState } from 'react'

export const CartContext = React.createContext();

export function CartProvider(props) {

    const [cartItems, setCartItems] = useState([]);
    const [clickedCart, setClickedCart] = useState(0);

    const addToCart = (product) => {
        setClickedCart(clickedCart + 1) // scroll on click to cart

        setCartItems(cartItems=>[...cartItems, product])
    }

    const cartCombine = Object.values(cartItems.reduce((a, {_id}) => {
        a[_id] = a[_id] || {_id, count: 0};
        a[_id].count++;
        return a;
    }, Object.create(null)));

    return (
        <CartContext.Provider
            value={{
                cartCombine: cartCombine,
                cartItems: cartItems,
                addToCart: addToCart,
                clickedCart: clickedCart
            }}
        >
            {props.children}
        </CartContext.Provider>
    )
}
