import React, { useEffect, useState } from 'react'
import '../../../../App.css'
import '../../../../Styles/Dashboard.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faGlobe } from '@fortawesome/free-solid-svg-icons'  
import { Pie } from '@reactchartjs/react-chart.js' 

export default function DashboardChartPie(props) {

    const products = props.products 
    const order = props.order 
    const [data, setData] = useState({})

    useEffect(()=>{
        if (products.length > 0 && order.length > 0) {
            const allCate = Object.values(products.reduce((a, {productCate}) => {
                a[productCate] = a[productCate] || {productCate, count: 0};
                return a;
            }, Object.create(null)));

            for (let i in allCate) {
                for (let j in products) {
                    if (allCate[i].productCate === products[j].productCate) { 
                        allCate[i].count += (products[j].productSold * products[j].productPrice)
                    }
                }
            }
            allCate.sort((a, b) => b.count - a.count)
            const topCate = allCate.splice(0, 5); 
            console.log(topCate)
            const topCateList = []
            for (let i in topCate) {
                topCateList.push(topCate[i].productCate)
            }
            setData({
                labels: ['Read', 'Unread'],
                datasets: [
                    {
                        label: '# of Votes',
                        data: [12, 88],
                        backgroundColor: [
                        'green',
                        'rgba(54, 162, 235, 0.9)', 
                        ],
                        borderColor: [
                        'rgba(255, 99, 132, 1)',
                        'rgba(54, 162, 235, 1)', 
                        ],
                        borderWidth: 1,
                    },
                ],
            })
        }
    },[products, order])  

    const options = {  
        responsive: true,
        maintainAspectRatio : false,
        // legend: {
        //     bottom: 0
        // },
        layout: {
            padding: { 
                top: '150%', 
            }
        }
    }

    return (
        <div className="chartpie flex-col">
            <div className="headerbox flex-center">
                <FontAwesomeIcon icon={faGlobe} className="icon"/>
            </div>
            <div className="top-location-container">
                <div className="headerbox-header">
                    <p>Email Statistics</p>
                </div>
                <div className="top-location-content flex">
                    <div className="top-location-map pie" style={{margin: '0'}}> 
                        <Pie data={data} options ={options}/> 
                    </div>
                </div>
            </div>
        </div>
    )
}
