import React, { useState } from 'react';
import '../App.css';

import ReactStars from "react-rating-stars-component";

export default function ProductReview(props) {
    const [tabId, setTabId] = useState(0);

    return(
        <div className="ProductReview">
            <div className="productreview-container">
                <div className="productreview-tab flex-center">
                    <div 
                        className={tabId === 0 ? "productreview-title search-tab-active" : "productreview-title"}
                        onClick={()=>{setTabId(0)}}>
                        Description
                    </div>
                    <div 
                        className={tabId === 1 ? "productreview-title search-tab-active" : "productreview-title"}
                        onClick={()=>{setTabId(1)}}>
                        Reviews
                        <span className={tabId === 1 ? "span-active" : ""}>
                            {props.productVote.length}
                        </span>
                    </div>
                </div>
                <div className="productreview-content">
                    {
                        tabId === 0 && 
                        <div className="productreview-text"> 
                            {props.productDes}
                        </div>
                    }
                    {
                        tabId === 1 && 
                        <div className="productreview-list"> 
                            {props.productVote.map((item, index) => {
                                const ratingStar = {
                                    size: 12,
                                    value: item.ratingStar,
                                    edit: false,
                                    activeColor: "#fda32a",
                                    color: "#ddd",
                                    isHalf: true
                                }
                                return (
                                    <div 
                                        className="productreview-item flex"
                                        key={index}
                                        >
                                        <div className="reviewer">
                                            <img src="https://scontent.fvca1-2.fna.fbcdn.net/v/t1.0-9/73321413_146697059956770_7174055866474168320_n.jpg?_nc_cat=107&_nc_sid=09cbfe&_nc_ohc=LxnVeUN0iWEAX-FROIZ&_nc_ht=scontent.fvca1-2.fna&oh=832b14ac63d67b7f58e34eec08fed9ad&oe=5F97B27C" width="100%" height="100%"></img>
                                        </div>
                                        <div className="review-info">
                                            <div className="review-first flex">
                                                <div className="reviewer-name">{item.name}</div>
                                                <div className="reviewer-ratingStar">
                                                    <ReactStars {...ratingStar} />
                                                </div>
                                            </div>
                                            <div className="review-second">
                                                {item.ratingDate}
                                            </div>
                                            <div className="review-third">
                                                {item.ratingText}
                                            </div>
                                        </div>
                                        <div className="productreview-line"></div>
                                    </div>
                                )
                            })}
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}
